import React from 'react';

class ListItem extends React.Component {
  constructor (props) {
    super(props);
    this.state = {
      isCompleted: false
    }
  }
  render() {
    return (
      <li>{this.props.item}<button  onClick={this.props.removeItem}>Delete</button></li>
    )
  }
}

class App extends React.Component {
  constructor (props) {
    super(props);
    this.state = {
      input: '',
      toDoList: []
    }
    this.handleChange = this.handleChange.bind(this);
    this.addItem = this.addItem.bind(this);
  }
  handleChange(event) {
    this.setState({input: event.target.value})
  }
  addItem(event) {
    event.preventDefault();
    this.setState(state => ({toDoList: [...state.toDoList, state.input]}));
  }
  removeItem(item, event) {
    event.preventDefault();
    this.setState(state => ({toDoList: state.toDoList.filter(el => el !== item)}));
    console.log(item);
  }
  render() {
    const toDoList = this.state.toDoList.map(item => <ListItem key={item} item={item} removeItem={this.removeItem.bind(this, item)}/>);
    return (
      <form>
        <input onChange={this.handleChange}>
        </input>
        <div>hi</div>
        <button onClick={this.addItem} type="submit">Button</button>
        <ul>
          {toDoList}
        </ul>
      </form>
    )
  }
}

export default App;
