import React from 'react';
import ListItem from './ListItem'

const Center = {
  textAlign: "center",
  marginTop: "40px"
}

class App extends React.Component {
  constructor (props) {
    super(props);
    this.state = {
      input: '',
      toDoList: []
    }
    this.handleChange = this.handleChange.bind(this);
    this.addItem = this.addItem.bind(this);
  }
  handleChange(event) {
    this.setState({input: event.target.value})
  }
  addItem(event) {
    event.preventDefault();
    this.setState(state => ({toDoList: [...state.toDoList, state.input]}));
  }
  removeItem(item, event) {
    event.preventDefault();
    this.setState(state => ({toDoList: state.toDoList.filter(el => el !== item)}));
    console.log(item);
  }
  render() {
    const toDoList = this.state.toDoList.map(item => <ListItem key={item} item={item} removeItem={this.removeItem.bind(this, item)}/>);
    return (
      <div style={this.Center}>
        <form>
          <input onChange={this.handleChange}>
          </input>
          <button onClick={this.addItem} type="submit">Button</button>
          <ul>
            {toDoList}
          </ul>
        </form>
      </div>
    )
  }
}

export default App;
