import React from 'react';

let listStyle = {
  textDecoration: "line-through"
}

class ListItem extends React.Component {
  constructor (props) {
    super(props);
    this.state = {
      isCompleted: false
    }
    this.handleClick = this.handleClick.bind(this);
  }
  handleClick(event) {
    event.preventDefault();
    this.setState({isCompleted: !this.state.isCompleted});
  }
  render() {
    this.state.isCompleted ? listStyle.textDecoration = "line-through" : listStyle.textDecoration =  "none";
    return (
      <li style={listStyle} onClick={this.handleClick}>{this.props.item}<button  onClick={this.props.removeItem}>Delete</button></li>
    )
  }
}

export default ListItem;
