import React from 'react';
import './CSS/App.css';

const ListItem = function(props) {
  return <li>{props.item}<a href='#' onClick={props.removeItem}>x</a></li>;
}

class App extends React.Component {
  constructor (props) {
    super(props);
    this.state = {
      input: '',
      toDoList: []
    }
    this.handleChange = this.handleChange.bind(this);
    this.addItem = this.addItem.bind(this);
    this.removeItem = this.removeItem.bind(this);
  }
  handleChange(event) {
    this.setState({input: event.target.value})
  }
  addItem(event) {
    event.preventDefault();
    this.setState(state => ({toDoList: [...state.toDoList, state.input]}));
  }
  removeItem(id) {
    id.preventDefault();
    this.setState(state => ({toDoList: state.toDoList.filter(el => el !== id)}));
    //console.log(id);
  }
  render() {
    const toDoList = this.state.toDoList.map(item => <ListItem key={item} item={item} removeItem={this.removeItem}/>);
    return (
      <form>
        <input onChange={this.handleChange}>
        </input>
        <button onClick={this.addItem} type="submit">Button</button>
        <ul>
          {toDoList}
        </ul>
      </form>
    )
  }
}

export default App;
